/**
 * Usage:
 *
 Request.GET(url='https://...').then(res => {
  if(res.status === 200) {
    console.log(res.body)
  }
}).catch(err => {
  console.error(err)
})
 */
export declare const Request: {
    urlBuilder(route: string): string;
    request(method: string, url: string, getOptions?: any): Promise<{
        ok: boolean;
        status: number;
        body: any;
        error?: undefined;
    } | {
        status: number;
        error: any;
        ok?: undefined;
        body?: undefined;
    }>;
    GET(url: string, options?: any): Promise<{
        ok: boolean;
        status: number;
        body: any;
        error?: undefined;
    } | {
        status: number;
        error: any;
        ok?: undefined;
        body?: undefined;
    }>;
    POST(url: string, body: any, options?: any): Promise<{
        ok: boolean;
        status: number;
        body: any;
        error?: undefined;
    } | {
        status: number;
        error: any;
        ok?: undefined;
        body?: undefined;
    }>;
    DELETE(url: string, options?: any): Promise<{
        ok: boolean;
        status: number;
        body: any;
        error?: undefined;
    } | {
        status: number;
        error: any;
        ok?: undefined;
        body?: undefined;
    }>;
};
